mapper.select:
SELECT id, person_id, title, journal, index_code, publish_date, doi, remark
FROM research_paper


        AND title LIKE CONCAT('%', #{title}, '%')


        AND person_id = #{personId}


        AND index_code = #{indexCode}


ORDER BY id DESC

mapper.update:
UPDATE research_paper
SET person_id=#{personId}, title=#{title}, journal=#{journal},
    index_code=#{indexCode}, publish_date=#{publishDate}, doi=#{doi}, remark=#{remark}
WHERE id=#{id}
