mapper.select:
SELECT p.id, p.person_id, p.title, p.journal, p.index_code, p.publish_date, p.doi, p.remark, rp.emp_no AS empNo, rp.name AS personName FROM research_paper p LEFT JOIN research_person rp ON rp.id = p.person_id
AND p.title LIKE CONCAT('%', #{title}, '%')
AND p.person_id = #{personId}
AND p.index_code = #{indexCode}
ORDER BY p.id DESC

SELECT COUNT(1)
FROM research_paper p


        AND p.title LIKE CONCAT('%', #{title}, '%')


        AND p.person_id = #{personId}


        AND p.index_code = #{indexCode}

mapper.update:
UPDATE research_paper
SET person_id=#{personId}, title=#{title}, journal=#{journal},
    index_code=#{indexCode}, publish_date=#{publishDate}, doi=#{doi}, remark=#{remark}
WHERE id=#{id}
