mapper.select:
SELECT id, emp_no, name, gender, title, department, phone, email, hire_date, remark
FROM research_person
WHERE id = #{id}

mapper.update:
UPDATE research_person
SET emp_no=#{empNo}, name=#{name}, gender=#{gender}, title=#{title}, department=#{department},
    phone=#{phone}, email=#{email}, hire_date=#{hireDate}, remark=#{remark}
WHERE id = #{id}
