mapper.select:
SELECT id, username, password_hash, real_name, emp_no AS empNo, gender, title, department, phone, email, hire_date AS hireDate, remark, role, status, created_at, updated_at FROM sys_user
AND username LIKE CONCAT('%', #{username}, '%')
AND real_name LIKE CONCAT('%', #{realName}, '%')
AND role = #{role}
AND status = #{status}
ORDER BY id ASC
